import axios from 'axios';

// Fungsi untuk memanggil backend FastAPI
export const generateNextQuestion = async (userId, role, level, answers) => {
  // answers = [{main_question_id: 1, answer_text: "Jawaban Q1"}, ...]
  try {
    const response = await axios.post(
      'http://127.0.0.1:8000/api/v1/questions/answers',
      {
        user_id: userId,
        role: role,
        level: level,
        answers: answers  // PENTING: harus ada
      }
    );
    return response.data;
  } catch (error) {
    console.error('Gagal memanggil API:', error);
    throw error;
  }
};
