import React, { useState } from 'react';

// ASUMSI: File generateNextQuestion berada di src/api/interviewApi.js
import { generateNextQuestion } from './api/interviewApi'; 

// --- PERBAIKAN DI SINI: Data Array Wajib Diisi ---
// Data Peran (Role) sesuai ref_role:
const roles = [
  'Android Developer',
  'Backend Developer',
  'Data Analyst',
  'Data Engineer',
  'Data Scientist',
  'DevOps',
  'Frontend Developer',
  'Fullstack Developer'
];

const levels = [
  'Junior',
  'Mid-Level',
  'Senior'
];
// -------------------------------------------------------------

function App() {
    // State untuk input user
    const [userId] = useState(1); // ID user testing
    
    // Perbaikan: Menggunakan nama state yang unik (selectedRole, setSelectedRole)
   const [selectedRole, setSelectedRole] = useState(roles[0]); 
    const [selectedLevel, setSelectedLevel] = useState(levels[0]);
    
    // State untuk hasil dari backend AI
    const [currentQuestion, setCurrentQuestion] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    // Fungsi untuk memicu panggilan API ke FastAPI
    const handleStartInterview = async () => {
        setIsLoading(true);
        setError(null);
        setCurrentQuestion(null); 

        try {
            console.log(`Memanggil AI untuk Role: ${selectedRole}, Level: ${selectedLevel}`);
            
            // Panggilan ke API Python FastAPI Anda (port 8000)
            const data = await generateNextQuestion(userId, selectedRole, selectedLevel);

            setCurrentQuestion({
                id: data.ml_question_id,
                text: data.question,
            });

        } catch (err) {
            console.error("Kesalahan Start Interview:", err);
            setError("Gagal mendapatkan pertanyaan dari AI. Pastikan server Python (port 8000) berjalan.");
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
            <h1>AI Interview Simulation Platform</h1>
            <p>Pilih peran dan tingkat kesulitan untuk memulai sesi wawancara generatif (User ID: {userId}).</p>

            {/* Area Pilihan Role dan Level */}
            <div style={{ marginBottom: '20px' }}>
                <label>Peran (Role): </label>
                <select value={selectedRole} onChange={(e) => setSelectedRole(e.target.value)} style={{ marginRight: '20px', padding: '5px' }}>
                    {/* Map data array roles yang sudah diisi */}
                    {roles.map(role => (
                        <option key={role} value={role}>{role}</option>
                    ))}
                </select>

                <label>Level: </label>
                <select value={selectedLevel} onChange={(e) => setSelectedLevel(e.target.value)} style={{ padding: '5px' }}>
                    {/* Map data array levels yang sudah diisi */}
                    {levels.map(level => (
                        <option key={level} value={level}>{level}</option>
                    ))}
                </select>

                <button 
                    onClick={handleStartInterview} 
                    disabled={isLoading} 
                    style={{ marginLeft: '20px', padding: '8px 15px', cursor: 'pointer' }}
                >
                    {isLoading? 'Menghubungi AI...' : 'Mulai Wawancara AI'}
                </button>
            </div>

            {/* Area Status dan Pertanyaan */}
            {error && <p style={{ color: 'red' }}>Error: {error}</p>}
            
            {currentQuestion && (
                <div style={{ border: '1px solid #007bff', padding: '15px', marginTop: '20px', backgroundColor: '#e9f7ff' }}>
                    <h2>Pertanyaan AI #{currentQuestion.id}</h2>
                    <p style={{ fontSize: '1.1em', fontWeight: 'bold' }}>{currentQuestion.text}</p>
                </div>
            )}
        </div>
    );
}

export default App;
