import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { generateNextQuestion } from './api/interviewApi';

const roles = [
  'Android Developer', 'Backend Developer', 'Data Analyst',
  'Data Engineer', 'Data Scientist', 'DevOps',
  'Frontend Developer', 'Fullstack Developer'
];
const levels = ['Junior', 'Mid-Level', 'Senior'];

function App() {
  const [userId] = useState(1);
  const [selectedRole, setSelectedRole] = useState(roles[0]);
  const [selectedLevel, setSelectedLevel] = useState(levels[0]);

  const [baseQuestions, setBaseQuestions] = useState([]);
  const [answers, setAnswers] = useState([]);
  const [generatedQuestions, setGeneratedQuestions] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Ambil 2 pertanyaan dasar saat role/level berubah
  useEffect(() => {
    const fetchBaseQuestions = async () => {
      try {
        setIsLoading(true);
        const res = await axios.get('http://127.0.0.1:8000/api/v1/questions/base', {
          params: { role: selectedRole, level: selectedLevel }
        });
        setBaseQuestions(res.data);
        setAnswers(res.data.map(q => ({ main_question_id: q.id, answer_text: '' })));
        setCurrentIndex(0);
        setGeneratedQuestions([]);
      } catch (err) {
        console.error(err);
        setError('Gagal memuat pertanyaan dasar.');
      } finally {
        setIsLoading(false);
      }
    };
    fetchBaseQuestions();
  }, [selectedRole, selectedLevel]);

  const handleAnswerChange = (index, text) => {
    const updated = [...answers];
    updated[index].answer_text = text;
    setAnswers(updated);
  };

  const handleSubmitAnswers = async () => {
    setIsLoading(true);
    setError(null);
    try {
      // Kirim jawaban user ke backend + generate pertanyaan AI
      const data = await generateNextQuestion(userId, selectedRole, selectedLevel, answers);
      setGeneratedQuestions(data.generated_questions || []);
      setCurrentIndex(baseQuestions.length); // pindah ke pertanyaan AI
    } catch (err) {
      console.error(err);
      setError('Gagal mengirim jawaban ke backend.');
    } finally {
      setIsLoading(false);
    }
  };

  const renderQuestion = (index) => {
    // Pertanyaan dasar
    if (index < baseQuestions.length) {
      const q = baseQuestions[index];
      return (
        <div key={q.id} className="question-area">
          <h2>Pertanyaan #{index + 1}</h2>
          <p>{q.question}</p>
          <textarea
            value={answers[index]?.answer_text || ''}
            onChange={(e) => handleAnswerChange(index, e.target.value)}
            rows={4}
            style={{ width: '100%', marginTop: '8px' }}
          />
        </div>
      );
    }
    // Pertanyaan AI
    const aiIndex = index - baseQuestions.length;
    const q = generatedQuestions[aiIndex];
    return (
      <div key={`ai-${aiIndex}`} className="question-area" style={{ marginTop: '20px' }}>
        <h2>Pertanyaan AI #{baseQuestions.length + aiIndex + 1}</h2>
        <p>{q}</p>
      </div>
    );
  };

  return (
    <div className="interview-container">
      <h1>Simulasi Wawancara - {selectedRole} ({selectedLevel})</h1>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Pilihan role/level */}
      <div style={{ display: 'flex', gap: '15px', marginBottom: '20px' }}>
        <select value={selectedRole} onChange={(e) => setSelectedRole(e.target.value)}>
          {roles.map(role => <option key={role} value={role}>{role}</option>)}
        </select>
        <select value={selectedLevel} onChange={(e) => setSelectedLevel(e.target.value)}>
          {levels.map(level => <option key={level} value={level}>{level}</option>)}
        </select>
      </div>

      {baseQuestions.length > 0 && currentIndex < baseQuestions.length && (
        <div>
          {renderQuestion(currentIndex)}
          <button
            onClick={() => {
              if (currentIndex < baseQuestions.length - 1) {
                setCurrentIndex(currentIndex + 1);
              } else {
                handleSubmitAnswers();
              }
            }}
            disabled={!answers[currentIndex]?.answer_text.trim() || isLoading}
          >
            {currentIndex < baseQuestions.length - 1 ? 'Pertanyaan Berikutnya' : (isLoading ? 'Mengirim jawaban...' : 'Kirim Jawaban')}
          </button>
        </div>
      )}

      {/* Tampilkan pertanyaan AI */}
      {currentIndex >= baseQuestions.length && generatedQuestions.length > 0 && (
        <div>
          {generatedQuestions.map((q, idx) => (
            <div key={idx} style={{ border: '1px solid #007bff', borderRadius: '6px', padding: '10px', marginTop: '10px' }}>
              <h3>Pertanyaan AI #{baseQuestions.length + idx + 1}</h3>
              <p>{q}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
