import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { generateNextQuestion } from './api/interviewApi';

const roles = [
  'Android Developer', 'Backend Developer', 'Data Analyst',
  'Data Engineer', 'Data Scientist', 'DevOps',
  'Frontend Developer', 'Fullstack Developer'
];
const levels = ['Junior', 'Mid-Level', 'Senior'];

function App() {
  const [userId] = useState(1);
  const [selectedRole, setSelectedRole] = useState(roles[0]);
  const [selectedLevel, setSelectedLevel] = useState(levels[0]);

  const [baseQuestions, setBaseQuestions] = useState([]);
  const [generatedQuestions, setGeneratedQuestions] = useState([]);
  const [answers, setAnswers] = useState([]); // gabungan jawaban dasar + AI
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Ambil pertanyaan dasar saat role/level berubah
  useEffect(() => {
    const fetchBaseQuestions = async () => {
      try {
        setIsLoading(true);
        const res = await axios.get('http://127.0.0.1:8000/api/v1/questions/base', {
          params: { role: selectedRole, level: selectedLevel }
        });
        setBaseQuestions(res.data);

        // Inisialisasi jawaban dasar
        setAnswers(res.data.map(q => ({
          main_question_id: q.id,
          ml_question_id: null,
          answer_text: ''
        })));

        setCurrentIndex(0);
        setGeneratedQuestions([]);
      } catch (err) {
        console.error(err);
        setError('Gagal memuat pertanyaan dasar.');
      } finally {
        setIsLoading(false);
      }
    };
    fetchBaseQuestions();
  }, [selectedRole, selectedLevel]);

  const handleAnswerChange = (index, text) => {
    const updated = [...answers];
    updated[index].answer_text = text;
    setAnswers(updated);
  };

  const handleSubmitAnswers = async () => {
    setIsLoading(true);
    setError(null);
    try {
      // Kirim jawaban user ke backend & generate pertanyaan AI
      const data = await generateNextQuestion(userId, selectedRole, selectedLevel, answers);

      setGeneratedQuestions(data.generated_questions || []);

      // Tambahkan jawaban kosong untuk pertanyaan AI
      const aiAnswers = (data.generated_questions || []).map((q, idx) => ({
        main_question_id: null,
        ml_question_id: data.generated_questions_ids[idx], // wajib, jangan null
        answer_text: ''
      }));

      setAnswers(prev => [...prev.slice(0, baseQuestions.length), ...aiAnswers]);

      setCurrentIndex(baseQuestions.length); // pindah ke pertanyaan AI
    } catch (err) {
      console.error(err);
      setError('Gagal mengirim jawaban ke backend.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleFinalSubmit = async () => {
    setIsLoading(true);
    setError(null);
    try {
      // Pisahkan jawaban dasar & AI
      const payload = {
        user_id: userId,
        role: selectedRole,
        level: selectedLevel,
        answers: answers.filter(a => a.main_question_id),
        ai_answers: answers.filter(a => a.ml_question_id)
      };

      await axios.post('http://127.0.0.1:8000/api/v1/questions/answers', payload);

      alert('Jawaban berhasil dikirim!');
    } catch (err) {
      console.error(err);
      setError('Gagal mengirim jawaban akhir.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="interview-container">
      <h1>Simulasi Wawancara - {selectedRole} ({selectedLevel})</h1>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Pilihan role/level */}
      <div style={{ display: 'flex', gap: '15px', marginBottom: '20px' }}>
        <select value={selectedRole} onChange={(e) => setSelectedRole(e.target.value)}>
          {roles.map(role => <option key={role} value={role}>{role}</option>)}
        </select>
        <select value={selectedLevel} onChange={(e) => setSelectedLevel(e.target.value)}>
          {levels.map(level => <option key={level} value={level}>{level}</option>)}
        </select>
      </div>

      {/* Render semua pertanyaan */}
      {[...baseQuestions, ...generatedQuestions].map((q, idx) => {
        const isAI = idx >= baseQuestions.length;
        return (
          <div key={idx} className="question-area" style={{ marginTop: '20px' }}>
            <h2>{isAI ? `Pertanyaan AI #${idx - baseQuestions.length + 1}` : `Pertanyaan #${idx + 1}`}</h2>
            <p>{q.question || q}</p>
            {/* Textarea untuk semua pertanyaan */}
            <textarea
              value={answers[idx]?.answer_text || ''}
              onChange={(e) => handleAnswerChange(idx, e.target.value)}
              rows={4}
              style={{ width: '100%', marginTop: '8px' }}
              placeholder="Tulis jawaban Anda di sini..."
            />
          </div>
        );
      })}

      {/* Tombol Kirim Jawaban */}
      {answers.length > 0 && (
        <button
          onClick={handleFinalSubmit}
          disabled={isLoading || answers.some(a => !a.answer_text)}
          style={{ marginTop: '15px', padding: '10px 20px', cursor: 'pointer' }}
        >
          {isLoading ? 'Mengirim jawaban...' : 'Kirim Semua Jawaban'}
        </button>
      )}
    </div>
  );
}

export default App;
